import React, {useEffect,useState} from 'react';
import {useParams} from 'react-router-dom'
import axios from 'axios'
import Presentation from './Presentation'
import Footer from './Footer'
import {Container, Row, Col} from 'react-bootstrap'
const DetailVoiture = () => {
    const [data, setmaData] = useState([])
    const {id} = useParams()
    useEffect(() => {
        detailId()
    },[])

    const detailId = async() => {
        const urlGetById = `http://localhost:5000/login/recupereInfoVoiture/${id}`
        await axios.get(urlGetById,{headers:{"token":window.localStorage.token}}).then((res) => {
            console.log(res.data.getVoiture);
            setmaData([res.data.getVoiture])
            //setmaData(res.data.getVoiture)
            console.log(data)
            })
    }
    return (
        <div>
         <Presentation/>
            <h1> DetailVoiture</h1>
           <Container>
                 {data.map((voiture) => {
                return(
                    <div>
                        {/* <h1> {voiture.marque}</h1>
                        <h1> {voiture.marque}</h1>
                        <h1> {voiture.marque}</h1> */}
                        <Row>
                            <Col sm={11}> 
                              <Row>
                                <Col sm={4}>
                                     <div className="imageDetail1">
                                        <img src={voiture.image.url} className="imageDetail"/> 
                                     </div>
                                </Col>
                                <Col sm={4} style={{display:'flex', marginTop:50, marginLeft:30}}>
                                    <div>
                                        <p> Marque: {voiture.marque}</p>
                                        <p> Couleur: {voiture.couleur}</p>
                                        <p> Etat: {voiture.etat}</p>
                                        <p> Habitacle:{voiture.habitacle}</p>
                                        <p> indice:{voiture.indice}</p>
                                        <p> model: {voiture.model}</p>
                                        <p> nombre Portières{voiture.portières}</p>
                                    </div>
                                    <div style={{marginLeft:30}}>
                                        <p> Prix: {voiture.prix}</p>
                                        <p> {voiture.prixApproximatif}</p>
                                        <p> {voiture.puissance}</p>
                                        <p> {voiture.qualitéVoiture}</p>
                                        <p> {voiture.rapports}</p>
                                        <p> {voiture.typeVehicule}</p>
                                    </div>
                                </Col>
                              </Row>
                            </Col>
                        </Row>
                    </div>
                )
            })}
           </Container>
            <Footer/>
        </div>
    );
};

export default DetailVoiture;
